def handle_button_8_click():
    # Get the turf_id from entry_1
    turf_id = entry_1.get()

    # Check if turf_id is empty
    if not turf_id:
        messagebox.showerror("Error", "Turf ID cannot be empty.")
        return

    # Check if turf_id is in the booking table
    cursor.execute("SELECT booking_amount, payable_amount FROM bookings WHERE turf_id = ?", (turf_id,))
    row = cursor.fetchone()
    if row is None:
        messagebox.showerror("Error", "Invalid Turf ID.")
        return

    # Get the booking amount from entry_2
    try:
        additional_amount = int(entry_2.get())
    except ValueError:
        messagebox.showerror("Error", "Booking amount must be an integer.")
        return

    # Update the booking amount in the booking table
    new_booking_amount = row[0] + additional_amount
    cursor.execute("UPDATE bookings SET booking_amount = ? WHERE turf_id = ?", (new_booking_amount, turf_id))
    conn.commit()

    # Calculate the amount left
    amount_left = row[1] - new_booking_amount

    # Display the successful booking message and the amount left in a message box
    messagebox.showinfo("Booking Successful", f"The booking was successful.\n The amount left is Rs: {amount_left}")
